        private void txtB_KeyDown(object sender, KeyEventArgs e)
        {
            if (e.KeyCode == Keys.Enter)
            {
                txtB.SelectAll();
                string txt_find = txtB.Text.ToLower();
                if (!string.IsNullOrEmpty(txt_find))
                {
                    ptDM.Visible = false;
                    pnDM.Visible = false;
                    label1.BackColor = Color.FromArgb(224, 238, 238);
                    label2.BackColor = Color.FromArgb(224, 238, 238);
                    label3.BackColor = Color.FromArgb(224, 238, 238);
                    label4.BackColor = Color.FromArgb(224, 238, 238);
                    lbhistory.BackColor = Color.FromArgb(224, 238, 238);
                    ptHome.BackColor = Color.FromArgb(224, 238, 238);
                    ptPlay.BackColor = Color.FromArgb(224, 238, 238);
                    ptLove.BackColor = Color.FromArgb(224, 238, 238);
                    ptHDM.BackColor = Color.FromArgb(224, 238, 238);
                    ptbhistory.BackColor = Color.FromArgb(224, 238, 238);
